import React, { useState } from "react";

export default function ContactForm() {
  const [form, setForm] = useState({
    nombre: "",
    email: "",
    mensaje: "",
  });
  const [errors, setErrors] = useState({});
  const [sent, setSent] = useState(false);
  const [sending, setSending] = useState(false);

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((p) => ({ ...p, [name]: value }));
    // limpiar error al tipear
    setErrors((p) => ({ ...p, [name]: undefined }));
  };

  const validate = () => {
    const e = {};
    if (!form.nombre.trim()) e.nombre = "Ingresá tu nombre";
    if (!form.email.trim()) {
      e.email = "Ingresá tu email";
    } else {
      const ok = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(form.email);
      if (!ok) e.email = "Email inválido";
    }
    if (!form.mensaje.trim()) e.mensaje = "Contanos en qué te ayudamos";
    setErrors(e);
    return Object.keys(e).length === 0;
  };

  const onSubmit = async (ev) => {
    ev.preventDefault();
    if (!validate()) return;
    setSending(true);
    try {
      // Simulá envío — acá iría tu POST /api/contacto
      console.log("Contacto enviado:", form);
      await new Promise((r) => setTimeout(r, 500));
      setSent(true);
      setForm({ nombre: "", email: "", mensaje: "" });
    } catch (err) {
      // podrías setear un error global si tuvieras backend
      console.error(err);
    } finally {
      setSending(false);
    }
  };

  return (
    <section className="contact">
      <div className="contact-card">
        <div className="contact-grid">
          {/* Columna izquierda: info */}
          <div className="contact-info">
            <span className="label">Contacto</span>
            <h3 className="contact-title">Hablemos</h3>
            <p className="contact-sub">
              Estamos para ayudarte con medidas, materiales, tiempos de entrega
              y proyectos a medida.
            </p>

            <ul className="contact-list">
              <li><span className="ci">📍</span> Av. San Juan 2847, San Cristóbal, CABA</li>
              <li><span className="ci">⏰</span> Lun a Vie · 10:00–19:00</li>
              <li><span className="ci">✉️</span> info@hermanosjota.com.ar</li>
              <li><span className="ci">📞</span> +54 11 4567-8900</li>
              <li><span className="ci">📷</span> @hermanosjota_ba</li>
            </ul>

            <div className="contact-hint">
              Preferís WhatsApp? Escribinos y te respondemos a la brevedad.
            </div>
          </div>

          {/* Columna derecha: formulario */}
          <div className="contact-form">
            <form onSubmit={onSubmit} noValidate>
              <div className="form-field">
                <label htmlFor="nombre">Nombre</label>
                <input
                  id="nombre"
                  name="nombre"
                  placeholder="Tu nombre"
                  value={form.nombre}
                  onChange={onChange}
                  aria-invalid={!!errors.nombre}
                  aria-describedby={errors.nombre ? "err-nombre" : undefined}
                />
                {errors.nombre && (
                  <div id="err-nombre" className="helper error">{errors.nombre}</div>
                )}
              </div>

              <div className="form-field">
                <label htmlFor="email">Email</label>
                <input
                  id="email"
                  type="email"
                  name="email"
                  placeholder="tunombre@email.com"
                  value={form.email}
                  onChange={onChange}
                  aria-invalid={!!errors.email}
                  aria-describedby={errors.email ? "err-email" : undefined}
                />
                {errors.email && (
                  <div id="err-email" className="helper error">{errors.email}</div>
                )}
              </div>

              <div className="form-field">
                <label htmlFor="mensaje">Mensaje</label>
                <textarea
                  id="mensaje"
                  name="mensaje"
                  rows="5"
                  placeholder="Contanos sobre el mueble o proyecto que tenés en mente…"
                  value={form.mensaje}
                  onChange={onChange}
                  aria-invalid={!!errors.mensaje}
                  aria-describedby={errors.mensaje ? "err-mensaje" : undefined}
                />
                {errors.mensaje && (
                  <div id="err-mensaje" className="helper error">{errors.mensaje}</div>
                )}
              </div>

              <div className="actions">
                <button className="btn" type="submit" disabled={sending}>
                  {sending ? "Enviando…" : "Enviar mensaje"}
                </button>
              </div>

              {sent && (
                <div className="success">
                  ¡Gracias! Te contactaremos pronto.
                </div>
              )}
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}
