import React, { useEffect, useState } from "react";
import Hero from "./components/Hero";



function App() {
const [products, setProducts] = useState([]);
const [loading, setLoading] = useState(true);
const [error, setError] = useState(null);
const [selected, setSelected] = useState(null);
const [cart, setCart] = useState([]);


useEffect(() => {
async function load() {
try {
setLoading(true);
const data = await apiGet("/api/productos");
setProducts(data);
} catch (err) {
setError(err.message);
} finally {
setLoading(false);
}
}
load();
}, []);


return (
<div className="app">
<Navbar cartCount={cart.length} />
<Hero />


<main className="container">
<section id="productos">
<h2 style={{textAlign:"center",letterSpacing:".12em"}}>PRODUCTOS DESTACADOS</h2>
{loading && <p>Cargando…</p>}
{error && <p style={{ color: "red" }}>Error al cargar los productos: {error}</p>}
{!loading && !error && (
selected ? (
<ProductDetail
product={selected}
onBack={() => setSelected(null)}
onAddToCart={() => setCart(prev => [...prev, selected])}
/>
) : (
<ProductList products={products} onSelect={setSelected} />
)
)}
</section>


<hr style={{margin:"32px 0"}} />


<section id="contacto">
<h2 style={{textAlign:"center",letterSpacing:".12em"}}>PÁGINA DE CONTACTO</h2>
<div className="container" style={{maxWidth:680}}>
<ContactForm />
</div>
</section>
</main>


<Footer />
</div>
);
}


export default App;
