import React, { useState } from "react";

export default function ContactForm() {
  const [form, setForm] = useState({ nombre: "", email: "", mensaje: "" });
  const [sent, setSent] = useState(false);
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
    setErrors(prev => ({ ...prev, [name]: undefined }));
  };

  const validate = () => {
    const esEmail = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const errs = {};
    if (!form.nombre.trim()) errs.nombre = "Ingresá tu nombre.";
    if (!esEmail.test(form.email)) errs.email = "Ingresá un email válido.";
    if (form.mensaje.trim().length < 10) errs.mensaje = "Contanos un poco más (mín. 10 caracteres).";
    setErrors(errs);
    return Object.keys(errs).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validate()) return;
    console.log("Contacto enviado:", form);  // cumple consigna
    setSent(true);
    // si querés limpiar:
    // setForm({ nombre: "", email: "", mensaje: "" });
  };

  return (
    <div className="contact-card">
      <h3>Contacto</h3>
      <p className="contact-sub">Dejanos tu consulta y te respondemos a la brevedad.</p>

      <form onSubmit={handleSubmit} noValidate>
        <div className="field">
          <label htmlFor="nombre">Nombre</label>
          <input
            id="nombre" name="nombre" value={form.nombre}
            onChange={handleChange} autoComplete="name" required
          />
          {errors.nombre && <div className="error-text">{errors.nombre}</div>}
        </div>

        <div className="field">
          <label htmlFor="email">Email</label>
          <input
            id="email" type="email" name="email" value={form.email}
            onChange={handleChange} autoComplete="email" required
          />
          {errors.email && <div className="error-text">{errors.email}</div>}
        </div>

        <div className="field">
          <label htmlFor="mensaje">Mensaje</label>
          <textarea
            id="mensaje" name="mensaje" rows="5"
            value={form.mensaje} onChange={handleChange} required
          />
          <div className="help">Ej.: “Quiero saber medida y stock del modelo Pampa”.</div>
          {errors.mensaje && <div className="error-text">{errors.mensaje}</div>}
        </div>

        <div className="form-actions">
          <button type="submit" className="btn">Enviar</button>
          {/* Opcional: botón WhatsApp sin link real */}
          <a className="btn whatsapp" href="#contacto" onClick={(e)=>e.preventDefault()}>
            WhatsApp
          </a>
        </div>

        {sent && <div className="success">¡Gracias! Te contactaremos pronto.</div>}
      </form>
    </div>
  );
}
